  private static void Parte1(string rompecabezas)
  {
    var pisos = 0;
    var sotano = false;

    for (int i = 0; i < rompecabezas.Length; i++)
    {
      if (rompecabezas[i] == '(') pisos++;
      else pisos--;

      if (pisos == -1 && !sotano)
      {
        Console.WriteLine("\nRespuesta 2: ");
        Console.WriteLine(i + 1);
        sotano = true;
      }
    }


    Console.WriteLine("\nRespuesta 1: ");
    Console.WriteLine(pisos);
  }
